
public class Q008 {

	public static void main(String[] args) {
		String s = "";
		long[] data = grab(s,0);
		long max = data[0];
		for(int i = (int)data[1]; i < s.length(); i++) {
			if(s.charAt(i)=='0') {
				data = grab(s,i+1);
				i = (int)data[1]-1;
				continue;
			}
			data[0] /= s.charAt(i-13)-48;
			data[0] *= s.charAt(i)-48;
			max = Math.max(max, data[0]);
		}
		System.out.println(max);
	}
	
	public static long[] grab(String s, int index) {
		long current = 1;
		int i = index;
		for(; i < index+13 && i < s.length(); i++) {
			if(s.charAt(i)=='0')
				return grab(s,i+1);
			current *= s.charAt(i)-48;
		}
		return new long[]{current,i};
	}

}
